.admin-modal-overlay {
  align-items: center;
  background: rgba(0, 0, 0, 0.5);
  height: 100%;
  justify-content: center;
  left: 0;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 1000;
}

.detailed-item-modal-container {
  box-sizing: border-box !important;
  margin: 0 auto !important;
  max-height: 90vh !important;
  max-width: 75vw !important;
  overflow-x: hidden !important;
  overflow-y: auto !important;
  width: 75vw !important;
}

.form-error {
  color: var(--form-error-color);
  display: block;
  font-size: 0.8125rem;
  margin-top: 0.25rem;
}

.header-container {
  margin: 0 !important;
  max-width: none !important;
  padding: 0 !important;
  width: 100% !important;
}

.item-popup {
  background: var(--popup-bg-color, #ffffff) !important;
  border: none !important;
  border-radius: 12px !important;
  box-shadow: none !important;
  font-size: 15px !important;
  height: auto !important;
  line-height: 1.4 !important;
  margin: 0 !important;
  min-height: auto !important;
  overflow: hidden !important;
  padding: 0 !important;
  padding-bottom: 0 !important;
}

.item-popup-enhanced {
  background: var(--popup-enhanced-bg-color);
  border: var(--popup-border-width) solid var(--popup-border-color);
  border-radius: var(--popup-enhanced-border-radius);
  box-shadow: var(--popup-enhanced-shadow);
  max-width: var(--popup-enhanced-max-width);
  min-width: var(--popup-enhanced-min-width);
  opacity: 0;
  padding: var(--popup-enhanced-padding);
  pointer-events: auto;
  position: absolute;
  transform: translateY(10px);
  transition: all 0.3s ease;
  visibility: hidden;
  width: auto;
  z-index: var(--z-popup);
}

.modal-room-page {
  background: transparent;
  height: 100%;
  overflow: visible;
  position: relative;
  transform-origin: top center;
  width: 100%;
  z-index: var(--z-base);
}

.nav-arrow {
  align-items: center;
  backdrop-filter: blur(4px);
  background: rgb(0 0 0 / 30%);
  border: none;
  border-radius: 50%;
  box-shadow: 0 4px 12px rgb(0 0 0 / 15%);
  color: white;
  cursor: pointer;
  display: flex;
  height: 50px;
  justify-content: center;
  position: fixed;
  top: 50%;
  transform: translateY(-50%);
  transition: all 0.2s ease;
  width: 50px;
  z-index: var(--z-modal-overlay, 60);
}

.room-modal-container {
  aspect-ratio: 1280 / 896;
  background: white;
  border-radius: 12px;
  box-shadow: 0 20px 25px -5px rgb(0 0 0 / 10%), 0 10px 10px -5px rgb(0 0 0 / 4%);
  margin-bottom: 5px !important;
  overflow: hidden;
  position: relative;
  transform: scale(0.95);
  transition: transform 0.3s ease;
  width: min(95vw, min(95vh * 1.43, 1200px));
}

.room-modal-header {
  box-sizing: border-box;
  left: 0;
  max-width: 100%;
  overflow: hidden;
  pointer-events: none;
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
  z-index: var(--z-index-room-modal-header, 2450);
}

.room-modal-iframe-container {
  height: 100%;
  width: 100%;
}

.room-modal-loading {
  align-items: center;
  background: #f8fafc;
  border-radius: 12px;
  display: flex;
  inset: 0;
  justify-content: center;
  position: absolute;
  z-index: var(--z-index-modal-content, 420);
}

.room-modal-overlay {
  align-items: center;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  inset: 0;
  justify-content: center;
  opacity: 0;
  padding-bottom: 0 !important;
  position: fixed;
  transition: all 0.3s ease;
  visibility: hidden;
  z-index: var(--z-index-room-modal, 2400);
}

